import os
import time
import datetime
import cv2
from cv_bridge import CvBridge
from sensor_msgs.msg import Image
from rclpy.qos import QoSProfile, ReliabilityPolicy, DurabilityPolicy, HistoryPolicy

class PhotoTaker:
    def __init__(self, node, topic="/bcr_bot/kinect_camera/image_raw"):
        self.node = node
        self.topic = topic
        self.bridge = CvBridge()
        self.latest_frame = None

        # í¼ë¸”ë¦¬ì…” QoSì™€ ë™ì¼í•˜ê²Œ ë§ì¶¤: RELIABLE / VOLATILE / KEEP_LAST(depth=10)
        qos = QoSProfile(depth=10)
        qos.reliability = ReliabilityPolicy.RELIABLE
        qos.durability  = DurabilityPolicy.VOLATILE
        qos.history     = HistoryPolicy.KEEP_LAST

        self.sub = node.create_subscription(
            Image,
            self.topic,
            self.image_callback,
            qos
        )
        self.node.get_logger().info(f"ğŸ“¸ PhotoTaker subscribed: {self.topic} (RELIABLE/VOLATILE)")

    def image_callback(self, msg: Image):
        try:
            self.latest_frame = self.bridge.imgmsg_to_cv2(msg, desired_encoding='bgr8')
        except Exception as e:
            self.node.get_logger().error(f"Image conversion failed: {e}")

    def take_photo(self, save_dir="/home/changwoo/ros2_ws/photos", wait_timeout=3.0):
        # ì²« í”„ë ˆì„ ëŒ€ê¸° (ì½œë°± ì²˜ë¦¬ ìœ„í•´ spin_once í•„ìš”)
        start = time.time()
        while self.latest_frame is None and (time.time() - start) < wait_timeout:
            # ë…¸ë“œì˜ ìŠ¤í•€ì„ ì ê¹ ëŒë ¤ ì½œë°±ì„ ì²˜ë¦¬
            import rclpy
            rclpy.spin_once(self.node, timeout_sec=0.1)

        if self.latest_frame is None:
            self.node.get_logger().warn("âš ï¸ No image received yet after waiting.")
            return None

        os.makedirs(save_dir, exist_ok=True)
        now = datetime.datetime.now().strftime("%Y%m%d_%H%M%S")
        filename = os.path.join(save_dir, f"photo_{now}.png")
        ok = cv2.imwrite(filename, self.latest_frame)
        if ok:
            self.node.get_logger().info(f"âœ… Saved photo: {filename}")
            return filename
        else:
            self.node.get_logger().error(f"âŒ Failed to save photo: {filename}")
            return None
